DROP TABLE IF EXISTS `sys_user`;
CREATE TABLE `sys_user` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `login_name` varchar(255) DEFAULT '' COMMENT '登录名称',
  `display_name` varchar(255) DEFAULT '' COMMENT '显示名称',
  `password` varchar(255) DEFAULT '' COMMENT '登录密码',
  `secret_degree` varchar(255) DEFAULT '非密' COMMENT '密级程度,值为机密，秘密，普通商密，核心商密，非密',
  `mobile` varchar(255) DEFAULT '' COMMENT '手机号码',
  `telephone` varchar(255) DEFAULT '' COMMENT '座机',
  `location` varchar(255) DEFAULT '' COMMENT '位置',
  `gender` varchar(255) DEFAULT '男' COMMENT ' 性别',
  `email` varchar(255) DEFAULT '' COMMENT '邮箱',
  `status` varchar(255) DEFAULT '正常' COMMENT '使用状态，正常和禁用',
  `sort` double(10,2) DEFAULT '0.00' COMMENT '根据该字段进行排序显示',
  `create_datetime` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `remark` varchar(500) DEFAULT '' COMMENT '备注',
  `dept_id` int(11) DEFAULT '0' COMMENT '部门id',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=21 DEFAULT CHARSET=utf8 COMMENT='用户表';

